#include <stdio.h>
int main() { int menu, tong1, tong2, thang, min, max, i, so, tong; do { printf("\n\n---------[MENU]---------\n1. Tinh tong 2 so nguyen a/b\n2. Nhap thang trong nam -> Thang thuoc quy nao\n3. Nhap mix max -> So chan min>max\n4. Nhap so nguyen N -> In ra tong tu 0->5\n5. Cut"); printf("\n\nNhap so cua ban (1 -> 5):"); scanf("%d", &menu); switch (menu) { case 1: printf("\nNhap 1 so di ban:"); scanf("%d", &tong1); printf("\nNgoan lam, nhap tiep so 2:"); scanf("%d", &tong2); printf("Tong 2 so nguyen la: %d", tong1+tong2); break;  case 2: printf("Nhap thang di ban: "); scanf("%d", &thang); if(thang > 0) { if(thang < 3) printf("Thang nay thuoc quy 1"); else if(thang < 6) printf("Thang nay thuoc quy 2"); else if(thang < 9) printf("Thang nay thuoc quy 3"); else if(thang < 12) printf("Thang nay thuoc quy 4"); } break;   case 3: printf("\nNhap min di ban: "); scanf("%d", &min); printf("\nNhap max di ban: "); scanf("%d", &max); i = min; while(i < max) { if(i%2 == 0) { printf("So chan: %d", i); } i++; } break;  case 4: printf("Nhap so: "); scanf("%d", &i); tong = 0; for(int dep = 0; dep < i++;) { if(i%2==0) { tong=tong + i; } printf("Tong ", tong); } break; case 5:printf("\n No, pp"); break; }} while (menu != 5); return 0;}